import React, { useEffect, useRef } from "react";
import $ from "jquery";

export function ScrollComponent() {
  const bodyRef = useRef(null);
  const panelRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const $window = $(window);
      const $body = $(bodyRef.current);
      const $panel = $(panelRef.current);

      const scroll = $window.scrollTop() + $window.height() / 3;

      $panel.each(function () {
        const $this = $(this);

        if (
          $this.position().top <= scroll &&
          $this.position().top + $this.height() > scroll
        ) {
          $body.removeClass(function (index, css) {
            return (css.match(/(^|\s)color-\S+/g) || []).join(" ");
          });

          $body.addClass("color-" + $this.data("color"));
        }
      });
    };

    $(window).on("scroll", handleScroll);

    // Trigger initial scroll to set the initial state
    handleScroll();

    // Cleanup: Remove scroll event listener when the component is unmounted
    return () => {
      $(window).off("scroll", handleScroll);
    };
  }, []);

  return (
    <div>
      {/* Your panel element with a 'data-color' attribute */}
      <div ref={panelRef} data-color="your-color">
        Panel Content
      </div>

      {/* Body element to apply classes */}
      <div ref={bodyRef}>{/* Your main content here */}</div>
    </div>
  );
}
